mapper.insert:
/* ClubMapper.insertClub */
insert into club.club (
	  club_nm
	, club_total_cnt
	, club_Intro
	, club_img_no
	, club_join_tp
	, action_area_lv1
	, action_area_lv2
	, club_category
	, account
	, bank_cd
	, frst_reg_no
	, frst_reg_dt
	, last_upd_no
	, last_upd_dt
) values {
	  #{clubNm}
	, #{clubTotalCnt}
	, #{clubIntro}
	, #{clubImgNo}
	, #{clubJoinTp}
	, #{actionAreaLv1}
	, #{actionAreaLv2}
	, #{clubCategory}
	, #{account}
	, #{bankCd}
	, #{memberNo}
	, now()
	, #{memberNo}
	, now()
}

mapper.update:
/* ClubMapper.updateClub */
update club.club
   set club_nm			= #{clubNm}
     , club_total_cnt	= #{clubTotalCnt} 
     , club_Intro		= #{clubIntro}
     , club_img_no		= #{clubImgNo}
     , club_join_tp		= #{clubJoinTp}
     , use_yn			= #{useYn}
     , open_yn			= #{openYn}
     , action_area_lv1	= #{actionAreaLv1}
     , action_area_lv2	= #{actionAreaLv2}
     , club_category 	= #{clubCategory}
     , del_yn			= #{delYn}
	 , account			= #{account}
	 , bank_cd			= #{bankCd}
	 , last_upd_no 		= #{memberNo}
	 , last_upd_dt 		= now()
 where club_no = #{clubNo}

mapper.delete:
		/* ClubMapper.deleteClub */
		delete from club.club
 		 where club_no = #{clubNo}
