mapper.insert:
/*clubMapper.insertMeeting*/
insert into meeting(
	  club_no
	, host_no
	, title
	, dday
	, hour
	, minute
	, place
	, cost
	, place_url
	, attend_cnt
	, meeting_gbn
	, frst_reg_no
	, frst_reg_dt
)values(
	  #{clubNo}
	, #{hostNo}
	, #{title}
	, #{dday}
	, #{hour}
	, #{minute}
	, #{place}
	, #{cost}
	, #{placeUrl}
	, #{attendCnt}
	, #{meetingGbn}
	, #{userNo}
	, now()
)

/*meetingMapper.insertMeetingMember*/
insert into meeting_member(
	  meeting_no
	, member_no
	, attend_yn
	, after_party_yn
	, div_amount
	, calcul_yn
	, reg_no
	, reg_dt
)values(
	  #{meetingNo}
	, #{memberNo}
	, #{attendYn}
	, #{afterPartyYn}
	, #{divAmount}
	, #{calculYn}
	, #{userNo}
	, now()
)
