/*MemberMapper.insertClubMember*/
insert into club_member(
	  club_no
	, member_no
	, nick_nm
	, join_dt
	, memo
	, club_author_no
	, frst_reg_no
	, frst_reg_dt
)
select #{clubNo}
     , m.member_no 
     , m.nick_nm 
     , #{joinDt}
     , #{memo}
     , '9'
     , #{userNo}
     , now()
  from member m 
 where member_no = #{memberNo}
